import React, { Component } from 'react';
import './App.css';
import Testdad from './components/Testdad';
import {Container, Col, Input} from 'reactstrap';


class App extends Component {
  constructor (){
    super ();
    this.state ={
    inputText: '',
    itemList : [ 
      {title : 'tom' , isCheck: true},
      {title : 'jerry', isCheck: false},
      {title : 'meow', isCheck: false}
    ]
  }

  this.onKeyUp = this.onKeyUp.bind(this);
  this.onChange = this.onChange.bind(this);

}


  onItemClick(item){
      return(event) => {
        let isCheck = item.isCheck;
        const {itemList} = this.state;
        const value = itemList.indexOf(item);
        this.setState({
         itemList : [
          ...itemList.slice(0,value),
          {
            ...item,
            isCheck: !isCheck
          },
          ...itemList.slice(value + 1)
         ]
        })

      }
  }


  onKeyUp(event){
    if(event.keyCode === 13){
      let text = event.target.value;
      if(!text){return ;}
      text = text.trim();
      if(!text){return ;}
      this.setState({
        itemList :[
          ...this.state.itemList,
          {title : text , isCheck : false }
        ]
      })
    }

  }


  onChange(event){

    this.setState({
      inputText : event.target.value,
      ...this.state.itemList
    })
  }



  render() {
    const {itemList} = this.state
    if(itemList.length){

      return (
        <Container>
          <Col sm={{ size: 4, offset: 4 }}>

            <div className="App">
            <Input 
              type="text" 
              placeholder="Add new item" 
              value = {this.state.inputText}
              onChange = {this.onChange}
              onKeyUp = {this.onKeyUp} />

            <div className="Showlist">
              {itemList.length && itemList.map(
              (item, index) =>  
              <Testdad 
                key = {index} 
                name= {item} 
                onClick = {this.onItemClick(item)} />
              )}
            </div>
            </div>

          </Col>
        </Container>
      )
    }else{
      return(
        <div className = "App"> Nothing here</div>
      )
    }

  }
}

export default App;
