import React, { Component } from 'react';
import './App.css';
import Testdad from './components/Testdad';


class App extends Component {
  constructor (){
    super ();
    this.state ={
      itemList : [ 
      {title : 'tom' , isCheck: true},
      {title : 'jerry', isCheck: false},
      {title: 'nhieu tien', isCheck: false}]
  }
}


  onItemClick(item){
      return(event) =>{
        const isCheck = item.isCheck;
        const {itemList} = this.state
        const value = itemList.indexOf(item)
        this.setState({
         itemList:[
          ...itemList.slice(0,value),
          {
            ...itemList,
            isCheck : !isCheck
          },
          ...itemList.slice(value + 1)
         ]
        })

      }
  }


  render() {
    const {itemList} = this.state
    if(itemList.length){
      return (
        <div className="App">
          {itemList.length && itemList.map((item, index) => <Testdad key = {index} name= {item} onClick = {this.onItemClick} />)

          }
        </div>
      )
    }else{
      return(
        <div className = "App"> Nothing here</div>
      )
    }

  }
}

export default App;
