import React, { Component } from 'react';
import './App.css';
import Testdad from './components/Testdad';


class App extends Component {
  constructor (){
    super ();
    this.state =[
      {title : 'tom' , isCheck: true},
      {title : 'jerry', isCheck: false},
      {title: 'nhieu tien', isCheck: false}];
  }


  onItemClick(item){
      return(event) =>{
        const isCheck = item.isCheck;
        const value = this.state.item.indexOf(item)
        this.setState([
          ...item.slice(0,value),
          {
            title,
            isCheck : !isCheck
          },
          ...item.slice(value + 1)
        ])

      }
  }


  render() {
    return (
      <div className="App">
        {
          this.state.map((item, index) => (<Testdad key= {index} name = {item}  onClick ={this.onItemClick} />))
        }
      </div>
    );
  }
}

export default App;
